TRIG.register("ice_encased_cured", "regex", [[^The ice encasing (\w+) cracks and sloughs off in chunks\, shattering upon the ground\.$]], function() creplaceLine("<aquamarine>"..matches[2].." <red>ICED"); end, "ASCENDRIL_OFFENSE_TRACKING");
TRIG.register("ice_encased_applied", "regex", [[^Limned with ice\, (\w+) becomes encased in the Brand of the Frost\'s chilling grasp\.$]], function() TRACK.taff(matches[2], "shivering");    TRACK.taff(matches[2], "frozen"); TRACK.taff(matches[2], "ice_encased"); creplaceLine("<aquamarine>"..matches[2].." <green>ICED"); end, "ASCENDRIL_OFFENSE_TRACKING");